import React, { useEffect, useState } from 'react';

function Description(props) {

    const [dataObject, setDataObject] = useState(props.location.object);
    let slashIndex = props.location.pathname.indexOfEnd('description/');
    console.log(slashIndex);
    console.log(props.location.pathname)
    console.log(props.location.pathname.substring(0, slashIndex))



    function indexOfEnd(string) {
        var io = this.indexOf(string);
        return io == -1 ? -1 : io + string.length;
    }

    useEffect(() => {
        console.log(dataObject);
        if (dataObject === undefined) {
            let dataObject = {
                id: '',
                name: '',
                image: '',
                description: '',
                infoArr: [],
                subInfo: [],
                type: ''
            }

            // let fetchUrl = 'http://localhost:5000/movies/api/${}';

            // axios({
            //     method: 'GET',
            //     url: fetchUrl
            // }).then(res => {
            //     setDataList(res.data)

            // })
        }
    }, dataObject);


    return (
        <div id="description_container">
            <h1>Description</h1>
        </div>

    )



}

export default Description;
