import React, { useState } from 'react';
import { useEffect } from 'react';
import GridView from './grid_view/grid_view';
import ListView from './list_view/list_view';
import list_view_icon from '../../../Assets/UI Icons/list-view.svg';
import grid_view_icon from '../../../Assets/UI Icons/grid-view.svg';


function Views(props) {
    const [objectType, setObjectType] = useState(props.type);
    const [isMovieGridView, setIsMovieGridView] = useState(JSON.parse(sessionStorage.getItem(props.type + 'GridViewBoolean')) || false);
    const [loading, setLoading] = useState(true);


    useEffect(() => {

        /*
        *  Which ever view is selected, disable to corresponding button and reduce its opacity by 50%.
        *  The other button will maintain its 100% opacity, changes it occordingly based on the current view.
        */
        sessionStorage.setItem(props.type + 'GridViewBoolean', isActorGridView);
        let yep = null;
        if (props.type === 'actor') {
            yep = isActorGridView;
        } else {
            yep = isMovieGridView;
        }




        if (yep) {

            document.getElementById("list_view_button").disabled = false;
            document.getElementById("list_view_button").style.opacity = "1";

            document.getElementById("grid_view_button").disabled = true;
            document.getElementById("grid_view_button").style.opacity = "0.5";



        } else {
            document.getElementById("grid_view_button").disabled = false;
            document.getElementById("grid_view_button").style.opacity = "1";

            document.getElementById("list_view_button").disabled = true;
            document.getElementById("list_view_button").style.opacity = "0.5";

        }

    }, [isActorGridView, isMovieGridView, props.type]);


    useEffect(() => {
        if (props.data.length > 0) {
            setLoading(false);
        }
        setObjectType(props.type);

    }, [props]);


    function loadViews() {

        if (objectType) {
            return (
                <GridView data={props.data} />
            )
        } else {
            return (
                <ListView data={props.data} />
            )
        }
    }
    function changeViewOption() {
        console.log(props.type)
        if (props.type === 'actor') {
            return (
                <>
                    <input onClick={() => { setIsActorGridView((isActorGridView) => !isActorGridView) }} className="view_buttons" id="list_view_button" type="image" src={list_view_icon} alt="List view button" />
                    <input onClick={() => { setIsActorGridView((isActorGridView) => !isActorGridView) }} className="view_buttons" id="grid_view_button" type="image" src={grid_view_icon} alt="Grid view button" />
                </>
            )
        } else if (props.type === 'movie') {
            return (
                <>
                    <input onClick={() => { setIsMovieGridView((isMovieGridView) => !isMovieGridView) }} className="view_buttons" id="list_view_button" type="image" src={list_view_icon} alt="List view button" />
                    <input onClick={() => { setIsMovieGridView((isMovieGridView) => !isMovieGridView) }} className="view_buttons" id="grid_view_button" type="image" src={grid_view_icon} alt="Grid view button" />
                </>
            )
        }

    }

    return (
        <>
            <div id="display_options">
                {changeViewOption()}
            </div>

            {loading ? <h1>Loading</h1> : loadViews(props.type)}
        </>

    )

}
export default Views;
